mapper.select:
select s.id shirtId,s.title,s.linkUrl,s.sourceId,s.brandId,s.minPrice,s.maxPrice,s.design,s.sleeve,s.isActive,s.weight, 
	(select sb.name from shirt_brand sb where sb.id=s.brandId) brandName, 
	(select ss.name from shirt_source ss where ss.id=s.sourceId) sourceName, 
	s.imgType,s.shirtImg,s.status,s.createTime,s.updateTime,if(sl.clickNum is null, 0, sl.clickNum) clickNum, 
	(select group_concat(sc.name separator '/') from shirt_param sp, shirt_color sc where 
	sp.colorId=sc.id and sp.shirtId=s.id and sp.status=0) colorNames 
from shirt s left join shirt_log sl on sl.shirtId=s.id 


		s.id in(select distinct sp1.shirtId from shirt_param sp1 where sp1.colorId in(${colorIds}) and sp1.status=0) 


		and s.sourceId=#{sourceId} 


		and s.brandId=#{brandId} 


		and s.isActive=#{isActive} 


		and s.design=#{design} 


		and s.sleeve=#{sleeve} 


		and s.status=#{status} 


		and s.minPrice >= #{minPrice} 


		and s.maxPrice <= #{maxPrice} 


		and s.createTime >= #{beginTime} 


		and s.createTime <= #{endTime} 


		and s.weight >= #{minWeight} 


		and s.weight <= #{maxWeight} 



	order by ${orderCont} 


	limit ${(pageNum-1) * pageSize}, ${pageSize}

select count(s.id) from shirt s 


		s.id in(select distinct sp1.shirtId from shirt_param sp1 where sp1.colorId in(${colorIds}) and sp1.status=0) 


		and s.sourceId=#{sourceId} 


		and s.brandId=#{brandId} 


		and s.isActive=#{isActive} 


		and s.design=#{design} 


		and s.sleeve=#{sleeve} 


		and s.status=#{status} 


		and s.minPrice >= #{minPrice} 


		and s.maxPrice <= #{maxPrice} 


		and s.createTime >= #{beginTime} 


		and s.createTime <= #{endTime} 


		and s.weight >= #{minWeight} 


		and s.weight <= #{maxWeight} 



	limit ${(pageNum-1) * pageSize}, ${pageSize}

mapper.update:
update shirt_param 


		price=#{price},  


		status=#{status}, 

	updateTime=now() 



		shirtId=#{shirtId} 


		and colorId=#{colorId}

mapper.select:
select sc.id, sc.name, sc.order, sc.status from shirt_color sc 


		and sc.status=#{status} 


order by sc.order

select sb.id, sb.name, sb.order, sb.status from shirt_brand sb 


		and sb.status=#{status} 


order by sb.order

select ss.id, ss.name, ss.order, ss.status from shirt_source ss 


		and ss.status=#{status} 


order by ss.order

select pr.id, pr.minPrice, pr.maxPrice, pr.order, pr.status from price_range pr 


		and pr.status=#{status} 


order by pr.order
